<template>
    <div class="img-list" v-if="list.length">
        <div class="item" v-for="(item, i) in list" :key="i" :class="{ active: model.some((a) => a.id == item.id) }" @click="select(item)">
            <a-image
                :width="160"
                :height="160"
                :src="item.url"
                :preview="false"
                fallback="data:image/png;base64,"
            />
            <div class="name" :title="item.name">{{ item.name }}</div>
        </div>
    </div>
</template>
<script lang="ts">
import { defineComponent, PropType, ref, watch } from "vue"

import * as I from "@/api/interface"
const Props = {
    list: {
        type: Array as PropType<Array<I.file.baseRow>>,
        default() {
            return []
        }
    },
    modelValue: {
        type: Array as PropType<Array<I.file.baseRow>>,
        default() {
            return []
        }
    }
}
export default defineComponent({
    props: Props,
    setup(props, context) {
        const model = ref(props.modelValue)
        watch(
            () => props.modelValue,
            (val) => {
                model.value = val
            }
        )
        watch(model, (val) => {
            context.emit("update:modelValue", val)
        })
        const select = (data: I.file.baseRow) => {
            model.value = [data]
        }
        return { model, select }
    }
})
</script>
<style scoped lang="scss">
.img-list {
    height: 540px;
    .item {
        float: left;
        margin: 0 20px 20px 0;
        border: 1px solid transparent;
        cursor: pointer;
        overflow: hidden;
        padding: 2px;
        width: 160px;
        &.active {
            border-color: #1890ff;
        }
        .name {
            white-space: nowrap;
            text-overflow: ellipsis;
            overflow: hidden;
        }
    }
    &:after {
        content: "";
        display: block;
        clear: both;
    }
}
</style>
